import React, { useState } from 'react';

const Room = () => {

    const [ lightStatus, setlightStatus] = useState(false)

    const statusRoom = () =>
    {
        if(lightStatus)
        {
            setlightStatus(false);
        }
        else
        {
            setlightStatus(true);
        }

    }
    return (
        <div>
            <button onClick= { statusRoom }>Lightswitch</button>
        </div>
    )
}

export default Room
